import numpy as np
import matplotlib.pyplot as plt

# Loading data generated by C files
data = np.loadtxt("output.txt")

x = data[:, 0]
f_x = data[:, 1]
pn_x = data[:, 2]
pm_x = data[:, 3]

# Plotting the results
plt.figure(figsize=(8, 6))
plt.plot(x, f_x, label='True Function f(x) = sin(2Ï€x)', linewidth=2)
plt.plot(x, pn_x, label='Interpolation P_n(x)', linestyle='--')
plt.plot(x, pm_x, label='Least Squares Fit P_m(x)', linestyle=':')
plt.legend()
plt.xlabel('x')
plt.ylabel('f(x)')
plt.title('Polynomial Interpolation and Least Squares Fitting')
plt.grid()
plt.show()